import java.awt.*;import java.awt.image.*;import java.applet.*;import java.util.*;public class GrowBox extends Control{	private	static	Color		lighterGray = new Color(239,239,239);	public GrowBox(NewWindow win)	{		theWindow = win;		local = false;		xPos = theWindow.width - 16;		yPos = theWindow.height- 15;		width = 14;		height = 14;		value = 0;		visible = true;		hiliteState = 255;	}		public void Draw()	{		xPos = theWindow.width - 16;		yPos = theWindow.height- 15;		theWindow.graphicsPortGC.setColor(Color.black);		theWindow.graphicsPortGC.drawLine(0,yPos-1,theWindow.width,yPos-1);		theWindow.graphicsPortGC.drawLine(theWindow.width-17,18,theWindow.width-17,theWindow.height);		if (hiliteState == 255)			theWindow.graphicsPortGC.drawImage(theWindow.theDesktop.growImage,xPos,yPos,null);		else		{			theWindow.graphicsPortGC.setColor(Color.white);			theWindow.graphicsPortGC.fillRect(xPos,yPos,14,14);		}	}	public void SetControlValue(int theValue)	{		Graphics	g;				value = theValue;	}}