import java.awt.*;import java.applet.*;import java.lang.*;import java.util.*;import java.awt.image.*;public class MAE extends Applet{	Desktop			theDesktop;	NewMenuBar		theMenuBar;	Frame				theFrame;	Application		ApplicationThread;	public void init()	{		setLayout(new BorderLayout());		ApplicationThread = new Application();		theDesktop = new Desktop(this);		add("Center", theDesktop);		theMenuBar = new NewMenuBar(this,theDesktop);		/* Find the frame containing the applet. */		Container parent = getParent();		while (parent != null &&  !(parent instanceof Frame))		{	   		parent = parent.getParent();		}		theFrame = (Frame)parent;		ApplicationThread.Setup(this);		ApplicationThread.start();			}		public boolean mouseDown(Event evt, int x, int y)	{		System.out.println("Down!");		return true;	}		public void paint(Graphics g)	{	}		public void Destroy()	{		int	i = 0;				ApplicationThread.RunLoop = false;		while (!ApplicationThread.RunLoop) i++;		ApplicationThread.stop();		ApplicationThread = null;		theDesktop.offScreenImage.flush();		theDesktop.offScreenImage = null;		theDesktop.offScreenImageGC.dispose();		theDesktop.offScreenImageGC = null;		theDesktop = null;	}}