import java.awt.*;import java.awt.image.*;import java.applet.*;import java.util.*;public class NewCheckBox extends Control{	private static	Font	helvFont = new Font("Helvetica", Font.BOLD, 12);	private			int		TitleWidth, TitleHeight;		public NewCheckBox()	{		super();	}		protected void SetSize()	{		FontMetrics	fm;		int			minWidth,minHeight;				theWindow.graphicsPortGC.setFont(helvFont);		fm = theWindow.graphicsPortGC.getFontMetrics(theWindow.graphicsPortGC.getFont());		TitleWidth = fm.stringWidth(Title);		minWidth = TitleWidth + 17;		TitleHeight = fm.getMaxAscent() + fm.getMaxDescent();		minHeight = TitleHeight;		if (width < minWidth) width = minWidth;		if (height < minHeight) height = minHeight;	}		protected void DrawIt()	{		if (hiliteState == 255) theWindow.graphicsPortGC.setColor(Color.black);		else theWindow.graphicsPortGC.setColor(Color.gray);		theWindow.graphicsPortGC.setFont(helvFont);		theWindow.graphicsPortGC.drawRect(xPos,yPos+1,11,11);		theWindow.graphicsPortGC.drawString(Title, xPos+17, yPos+11);				if (value != 0)		{			theWindow.graphicsPortGC.drawLine(xPos,yPos+1,xPos+11,yPos+12);			theWindow.graphicsPortGC.drawLine(xPos+11,yPos+1,xPos,yPos+12);		}		else		{			theWindow.graphicsPortGC.setColor(Color.white);			theWindow.graphicsPortGC.fillRect(xPos+1,yPos+2,10,10);		}	}		public int TrackControl(Point location)	{		int		x,y;		boolean	pushed;				pushed = false;				do		{			x = location.x;			y = location.y;			if (x >= xPos & x < xPos+width & y >= yPos & y < yPos+height)			{				if (!pushed)				{					push();					pushed = true;				}			}			else			{				if (pushed)				{					pull();					pushed = false;				}			} 			location.x = theWindow.AFC.xMousePos;			location.y = theWindow.AFC.yMousePos;			location = theWindow.GlobalToLocal(location);		}		while (!theWindow.AFC.WaitMouseUp());				if (pushed)		{			pull();			return 11;		}		else return 0;	}	public void push()	{		theWindow.graphicsPortGC.setColor(Color.black);			theWindow.graphicsPortGC.drawRect(xPos+1,yPos+2,9,9);		if (value != 0)		{			theWindow.graphicsPortGC.drawLine(xPos,yPos+1,xPos+11,yPos+12);			theWindow.graphicsPortGC.drawLine(xPos+11,yPos+1,xPos,yPos+12);		}		theWindow.UpdateGraphicsPort();	}	public void pull()	{		theWindow.graphicsPortGC.setColor(Color.white);			theWindow.graphicsPortGC.drawRect(xPos+1,yPos+2,9,9);		theWindow.graphicsPortGC.setColor(Color.black);			if (value != 0)		{			theWindow.graphicsPortGC.drawLine(xPos,yPos+1,xPos+11,yPos+12);			theWindow.graphicsPortGC.drawLine(xPos+11,yPos+1,xPos,yPos+12);		}		theWindow.UpdateGraphicsPort();	}}	