import java.awt.*;import java.awt.image.*;import java.applet.*;import java.util.*;public class myDocumentWindow extends DocumentWindow{	public	myDocumentWindow()	{		super();	}		public void Init()	{		Enumeration	e;		NewMItem	Temp;				e = theApplet.theMenuBar.GetMenu(2).GetMItem(0).AttachedMenu.myVector.elements();		while (e.hasMoreElements())		{			Temp = (NewMItem)e.nextElement();			Temp.CheckItem(false);		}		theApplet.theMenuBar.GetMenu(2).GetMItem(0).AttachedMenu.AddMItem(title,false);		Temp = (NewMItem)theApplet.theMenuBar.GetMenu(2).GetMItem(0).AttachedMenu.myVector.lastElement();		Temp.CheckItem(true);	}	public void userActivate()	{		Enumeration	e;		NewMItem	Temp;				e = theApplet.theMenuBar.GetMenu(2).GetMItem(0).AttachedMenu.myVector.elements();		while (e.hasMoreElements())		{			Temp = (NewMItem)e.nextElement();			if (title.equals(Temp.Title.text)) Temp.CheckItem(true);			else  Temp.CheckItem(false);		}	}	public void Close()	{		Enumeration	e;		NewMItem	Temp;		int			i;				i = 0;		e = theApplet.theMenuBar.GetMenu(2).GetMItem(0).AttachedMenu.myVector.elements();		while (e.hasMoreElements())		{			Temp = (NewMItem)e.nextElement();			if (title.equals(Temp.Title.text))theApplet.theMenuBar.GetMenu(2).GetMItem(0).AttachedMenu.DeleteMenuItem(i);			else  Temp.CheckItem(false);			i++;		}	}}