import java.awt.*;import java.awt.image.*;import java.applet.*;import java.util.*;public class noGrowDocWindow extends DocumentWindow{	public	noGrowDocWindow()	{		super();		highlighted = true;		drag = true;		zoombox = false;		growbox = false;		modal = false;	}		protected void DrawTitle()	{		if (highlighted)		{			titlePortGC.setColor(lighterGray);			titlePortGC.fillRect(0,0,width-1,18);			titlePortGC.setColor(lightBlue);			titlePortGC.drawLine(1,1,width-3,1);			titlePortGC.drawLine(1,2,1,16);			titlePortGC.setColor(midBlue);			titlePortGC.drawLine(1,17,width-2,17);			titlePortGC.drawLine(width-2,1,width-2,16);			titlePortGC.setColor(midGray);					for (int i=0; i<6; i++)titlePortGC.drawLine(2,4+(2*i),width-3,4+(2*i));			titlePortGC.setColor(Color.black);			titlePortGC.drawLine(1,18,width-1,18);			if (!title.equals(""))			{				titlePortGC.setColor(lighterGray);				titlePortGC.fillRect((width/2)-(titleWidth/2),4,titleWidth,13);				titlePortGC.setColor(Color.black);				titlePortGC.drawString(title,(width/2)-(titleWidth/2)+5,14);			}			if (closebox)			{				titlePortGC.setColor(lighterGray);				titlePortGC.fillRect(closeRect.x-1,4,13,11);				titlePortGC.drawImage(AFC.closeImage,closeRect.x,closeRect.y,null);			}		}		else		{			titlePortGC.setColor(Color.white);			titlePortGC.fillRect(0,0,width-1,18);			titlePortGC.setColor(borderGray);			titlePortGC.drawLine(1,18,width-1,18);						if (!title.equals(""))			{				titlePortGC.setColor(textGray);				titlePortGC.drawString(title,(width/2)-(titleWidth/2)+5,14);			}		}	}		public void DrawGrowIcon()	{	}	}